(
    sum(
        rate(
            traces_span_metrics_duration_milliseconds_count{
                service_name="$name",
                span_kind="SPAN_KIND_SERVER",
                http_status_code=~"^2.*|^3.*",
                http_route=~"$route"
            }[5m]
        ) or on() vector(0)
    )
    +
    sum(
        rate(
            traces_span_metrics_duration_milliseconds_count{
                service_name="$name",
                span_kind="SPAN_KIND_SERVER",
                rpc_grpc_status_code=~"0|1",
                http_route=~"$route"
            }[5m]
        ) or on() vector(0)
    )
)
* 100
/
sum(
    rate(
        traces_span_metrics_duration_milliseconds_count{
            span_kind="SPAN_KIND_SERVER",
            service_name="$name",
            http_route=~"$route"
        }[5m]
    )
)

(
  sum(
    rate(
      traces_span_metrics_duration_milliseconds_count{
        service_name="$name",
        span_kind="SPAN_KIND_SERVER",
        http_status_code!~"^2.*|^3.*",
        rpc_grpc_status_code="",
        http_status_code!=""
      }[5m]
    ) or on() vector(0)
  )
  +
  sum(
    rate(
      traces_span_metrics_duration_milliseconds_count{
        service_name="$name",
        span_kind="SPAN_KIND_SERVER",
        rpc_grpc_status_code!~"^0$",
        http_status_code="",
        rpc_grpc_status_code!=""
      }[5m]
    ) or on() vector(0)
  )
)
* 100
/
sum(
  rate(
    traces_span_metrics_duration_milliseconds_count{
      span_kind="SPAN_KIND_SERVER",
      service_name="$name",
      http_route=~"$route"
    }[5m]
  )
)

