targets.expr:
sum by (pod) (
  kafka_server_replicamanager_total_isrexpandspersec_count{
    cluster="$cluster", 
    namespace="$namespace", 
    job="$job", 
    pod=~"$pod"
  }
)

sum by (pod) (
  kafka_log_log_size{
    cluster="$cluster",
    namespace="$namespace",
    job="$job",
    pod=~"${pod:regex}"
  }
) / 1024 / 1024 / 1024
